        if (navigator.userAgent.match('MSIE 8') == null) {
            mediaQueryId = mediaQueryId.replace(/"/g, '')
        }

        $container.isotope({
            resizable: true,
            itemSelector: '.related_gallery'
        }).isotope('reLayout')
    }

});
